				UPDATE PWD_HISTORY SET LOGIN_ID=newId WHERE SERVICE_ID=srvId AND MANAGED_SYS_ID=mgSysId AND LOGIN=lgn;
				SET newId = newId + 1;
			END IF;
		UNTIL done END REPEAT; 
				
		
		CLOSE cur1;
	END$$
