		DECLARE authLevelId VARCHAR(32);
		DECLARE entityId VARCHAR(32);
		DECLARE cur1 CURSOR FOR (SELECT URI_PATTERN_ID, MIN_AUTH_LEVEL FROM URI_PATTERN);
